import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../services/api.service';
import { Customer } from '../models';

@Component({
  selector: 'app-customer',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css']
})
export class CustomerComponent implements OnInit {
  customers: Customer[] = [];
  newCustomer: Customer = { name: '', email: '', phone: '', address: '' };
  editingId: number | null = null;
  showForm = false;
  showSuccess = false;
  successMessage = '';

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.loadCustomers();
  }

  loadCustomers(): void {
    this.apiService.getCustomers().subscribe({
      next: (data) => this.customers = data,
      error: (error) => console.error('Error loading customers:', error)
    });
  }

  addCustomer(): void {
    if (this.newCustomer.name && this.newCustomer.email) {
      this.apiService.createCustomer(this.newCustomer).subscribe({
        next: () => {
          this.loadCustomers();
          this.newCustomer = { name: '', email: '', phone: '', address: '' };
          this.showForm = false;
        },
        error: (error) => console.error('Error adding customer:', error)
      });
    }
  }

  deleteCustomer(id: number | undefined): void {
    if (id && confirm('Are you sure you want to delete this customer?')) {
      this.apiService.deleteCustomer(id).subscribe({
        next: () => this.loadCustomers(),
        error: (error) => console.error('Error deleting customer:', error)
      });
    }
  }

  toggleForm(): void {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.newCustomer = { name: '', email: '', phone: '', address: '' };
    }
  }
}
<div class="customer-container">
  <div class="header">
    <h2>Customer Management</h2>
    <button class="btn-primary" (click)="toggleForm()">
      {{ showForm ? '✕ Cancel' : '+ Add Customer' }}
    </button>
  </div>

  <div class="form-card" *ngIf="showForm">
    <h3>Add New Customer</h3>
    <div class="form-row">
      <div class="form-group">
        <label>Full Name *</label>
        <input [(ngModel)]="newCustomer.name" name="name" placeholder="Enter customer name" required>
      </div>
      <div class="form-group">
        <label>Email *</label>
        <input [(ngModel)]="newCustomer.email" name="email" type="email" placeholder="customer@example.com" required>
      </div>
    </div>
    <div class="form-row">
      <div class="form-group">
        <label>Phone</label>
        <input [(ngModel)]="newCustomer.phone" name="phone" placeholder="Enter phone number">
      </div>
      <div class="form-group">
        <label>Address</label>
        <input [(ngModel)]="newCustomer.address" name="address" placeholder="Enter address">
      </div>
    </div>
    <button type="button" (click)="addCustomer()" class="btn-success">Add Customer</button>
  </div>

  <div class="content">
    <h3>Customers List</h3>
    <div *ngIf="customers.length > 0; else noCustomers" class="cards-grid">
      <div *ngFor="let customer of customers" class="card">
        <div class="card-header">
          <h4>{{ customer.name }}</h4>
        </div>
        <div class="card-body">
          <p><strong>Email:</strong> {{ customer.email }}</p>
          <p><strong>Phone:</strong> {{ customer.phone || 'N/A' }}</p>
          <p><strong>Address:</strong> {{ customer.address || 'N/A' }}</p>
        </div>
        <div class="card-footer">
          <button (click)="deleteCustomer(customer.id)" class="btn-danger">Delete</button>
        </div>
      </div>
    </div>
    <ng-template #noCustomers>
      <div class="empty-state">
        <p>No customers found. Add a new customer to get started.</p>
      </div>
    </ng-template>
  </div>
</div>
