import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../services/api.service';
import { Booking, Customer, Driver, Vehicle } from '../models';

@Component({
  selector: 'app-booking',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './booking.component.html',
  styleUrls: ['./booking.component.css']
})
export class BookingComponent implements OnInit {
  bookings: Booking[] = [];
  customers: Customer[] = [];
  drivers: Driver[] = [];
  vehicles: Vehicle[] = [];
  
  selectedCustomerId: number | null = null;
  selectedDriverId: number | null = null;
  selectedVehicleId: number | null = null;
  
  newBooking: Booking = { 
    customer: { id: 0 },
    driver: { id: 0 },
    vehicle: { id: 0 },
    pickupLocation: '',
    dropoffLocation: '',
    pickupDateTime: '',
    dropoffDateTime: '',
    passengerCount: 1,
    totalAmount: 0,
    status: 'CONFIRMED'
  };
  
  newCustomerData: Customer = {
    name: '',
    email: '',
    phone: '',
    address: ''
  };

  showForm = false;
  useExistingCustomer = true;
  createdCustomerId: number | null = null;
  showSuccess = false;
  successMessage = '';

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.loadBookings();
    this.loadDropdownData();
  }

  loadBookings(): void {
    this.apiService.getBookings().subscribe({
      next: (data) => this.bookings = data,
      error: (error) => console.error('Error loading bookings:', error)
    });
  }

  loadDropdownData(): void {
    this.apiService.getCustomers().subscribe({
      next: (data) => this.customers = data,
      error: (error) => console.error('Error loading customers:', error)
    });

    this.apiService.getDrivers().subscribe({
      next: (data) => this.drivers = data,
      error: (error) => console.error('Error loading drivers:', error)
    });

    this.apiService.getVehicles().subscribe({
      next: (data) => this.vehicles = data,
      error: (error) => console.error('Error loading vehicles:', error)
    });
  }

  createNewCustomer(): void {
    if (this.newCustomerData.name && this.newCustomerData.email && this.newCustomerData.phone) {
      this.apiService.createCustomer(this.newCustomerData).subscribe({
        next: (customer) => {
          this.createdCustomerId = customer.id || 0;
          this.selectedCustomerId = this.createdCustomerId;
          this.newBooking.customer = { id: this.createdCustomerId };
          this.customers.push(customer);
          this.newCustomerData = { name: '', email: '', phone: '', address: '' };
          this.showSuccessMessage('Customer created successfully!');
          console.log('Customer created successfully', customer);
        },
        error: (error) => console.error('Error creating customer:', error)
      });
    } else {
      this.showSuccessMessage('Please fill in Name, Email, and Phone!');
    }
  }

  addBooking(): void {
    let customerId = this.useExistingCustomer ? this.selectedCustomerId : this.createdCustomerId;
    let driverId = this.selectedDriverId;
    let vehicleId = this.selectedVehicleId;

    if (customerId && driverId && vehicleId &&
        this.newBooking.pickupLocation && this.newBooking.dropoffLocation && 
        this.newBooking.pickupDateTime && this.newBooking.dropoffDateTime &&
        this.newBooking.passengerCount) {
      
      const bookingToCreate: Booking = {
        customer: { id: customerId },
        driver: { id: driverId },
        vehicle: { id: vehicleId },
        pickupLocation: this.newBooking.pickupLocation,
        dropoffLocation: this.newBooking.dropoffLocation,
        pickupDateTime: this.newBooking.pickupDateTime,
        dropoffDateTime: this.newBooking.dropoffDateTime,
        passengerCount: this.newBooking.passengerCount,
        totalAmount: 0,
        status: 'CONFIRMED'
      };
      
      console.log('Sending booking:', bookingToCreate);
      
      this.apiService.createBooking(bookingToCreate).subscribe({
        next: () => {
          this.loadBookings();
          this.resetForm();
          this.showSuccessMessage('Booking created successfully!');
        },
        error: (error) => {
          console.error('Error adding booking:', error);
          this.showSuccessMessage('Error creating booking. Check console.');
        }
      });
    } else {
      this.showSuccessMessage('Please fill in all required fields!');
    }
  }

  showSuccessMessage(message: string): void {
    this.successMessage = message;
    this.showSuccess = true;
    setTimeout(() => {
      this.showSuccess = false;
    }, 3000);
  }

  resetForm(): void {
    this.newBooking = { 
      customer: { id: 0 },
      driver: { id: 0 },
      vehicle: { id: 0 },
      pickupLocation: '',
      dropoffLocation: '',
      pickupDateTime: '',
      dropoffDateTime: '',
      passengerCount: 1,
      totalAmount: 0,
      status: 'CONFIRMED'
    };
    this.newCustomerData = { name: '', email: '', phone: '', address: '' };
    this.createdCustomerId = null;
    this.selectedCustomerId = null;
    this.selectedDriverId = null;
    this.selectedVehicleId = null;
    this.useExistingCustomer = true;
    this.showForm = false;
  }

  deleteBooking(id: number | undefined): void {
    if (id && confirm('Are you sure you want to cancel this booking?')) {
      this.apiService.deleteBooking(id).subscribe({
        next: () => this.loadBookings(),
        error: (error) => console.error('Error deleting booking:', error)
      });
    }
  }

  toggleForm(): void {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.resetForm();
    }
  }

  getCustomerName(id: number | undefined): string {
    return this.customers.find(c => c.id === id)?.name || 'N/A';
  }

  getDriverName(id: number | undefined): string {
    return this.drivers.find(d => d.id === id)?.name || 'N/A';
  }

  getVehicleModel(id: number | undefined): string {
    return this.vehicles.find(v => v.id === id)?.model || 'N/A';
  }

  getSelectedVehiclePrice(): number {
    if (!this.selectedVehicleId) return 0;
    const vehicle = this.vehicles.find(v => v.id === this.selectedVehicleId);
    return vehicle?.pricePerKm || 0;
  }

  getStatusClass(status: string): string {
    return status.toLowerCase().replace(' ', '-');
  }
}
<div class="booking-container">
  <div class="header">
    <h2>Booking Management</h2>
    <button class="btn-primary" (click)="toggleForm()">
      {{ showForm ? '✕ Cancel' : '+ New Booking' }}
    </button>
  </div>

  <div class="success-alert" *ngIf="showSuccess">
    ✓ {{ successMessage }}
  </div>

  <div class="form-card" *ngIf="showForm">
    <h3>Create New Booking</h3>
    
    <!-- Customer Section -->
    <div class="form-section">
      <h4>Customer Information</h4>
      <div class="toggle-options">
        <label class="radio-label">
          <input type="radio" [(ngModel)]="useExistingCustomer" [value]="true" name="customerType">
          <span>Select Existing Customer</span>
        </label>
        <label class="radio-label">
          <input type="radio" [(ngModel)]="useExistingCustomer" [value]="false" name="customerType">
          <span>Add New Customer</span>
        </label>
      </div>

      <!-- Existing Customer Selection -->
      <div *ngIf="useExistingCustomer" class="form-row">
        <div class="form-group">
          <label>Customer *</label>
          <select [(ngModel)]="selectedCustomerId" name="customer" required>
            <option [value]="null" disabled>Select a customer</option>
            <option *ngFor="let customer of customers" [value]="customer.id">
              {{ customer.name }} ({{ customer.email }})
            </option>
          </select>
        </div>
      </div>

      <!-- New Customer Form -->
      <div *ngIf="!useExistingCustomer" class="new-customer-section">
        <div class="form-row">
          <div class="form-group">
            <label>Customer Name *</label>
            <input [(ngModel)]="newCustomerData.name" name="cName" placeholder="Enter full name" required>
          </div>
          <div class="form-group">
            <label>Email *</label>
            <input [(ngModel)]="newCustomerData.email" name="cEmail" type="email" placeholder="customer@example.com" required>
          </div>
        </div>
        <div class="form-row">
          <div class="form-group">
            <label>Phone</label>
            <input [(ngModel)]="newCustomerData.phone" name="cPhone" placeholder="Phone number">
          </div>
          <div class="form-group">
            <label>Address</label>
            <input [(ngModel)]="newCustomerData.address" name="cAddress" placeholder="Address">
          </div>
        </div>
        <button type="button" (click)="createNewCustomer()" class="btn-create-customer">
          {{ createdCustomerId ? '✓ Customer Created' : 'Create Customer' }}
        </button>
      </div>
    </div>

    <!-- Booking Details Section -->
    <div class="form-section">
      <h4>Booking Details</h4>
      <div class="form-row">
        <div class="form-group">
          <label>Driver *</label>
          <select [(ngModel)]="selectedDriverId" name="driver" required>
            <option [value]="null" disabled>Select a driver</option>
            <option *ngFor="let driver of drivers" [value]="driver.id">
              {{ driver.name }} ({{ driver.licenseNumber }})
            </option>
          </select>
        </div>
        <div class="form-group">
          <label>Vehicle *</label>
          <select [(ngModel)]="selectedVehicleId" name="vehicle" required>
            <option [value]="null" disabled>Select a vehicle</option>
            <option *ngFor="let vehicle of vehicles" [value]="vehicle.id">
              {{ vehicle.model }} ({{ vehicle.registrationNumber }}) - ₹{{ vehicle.pricePerKm }}/km
            </option>
          </select>
        </div>
      </div>

      <div *ngIf="selectedVehicleId" class="price-info">
        <strong>Vehicle Price: ₹{{ getSelectedVehiclePrice() }}/km</strong>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label>Pickup Date & Time *</label>
          <input [(ngModel)]="newBooking.pickupDateTime" name="pickupDateTime" type="datetime-local" required>
        </div>
        <div class="form-group">
          <label>Dropoff Date & Time *</label>
          <input [(ngModel)]="newBooking.dropoffDateTime" name="dropoffDateTime" type="datetime-local" required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label>Passenger Count *</label>
          <input [(ngModel)]="newBooking.passengerCount" name="passengerCount" type="number" min="1" required>
        </div>
        <div class="form-group">
          <label>Total Amount *</label>
          <input [(ngModel)]="newBooking.totalAmount" name="totalAmount" type="number" min="0" step="0.01" required>
        </div>
      </div>
    </div>

    <!-- Location Section -->
    <div class="form-section">
      <h4>Trip Location</h4>
      <div class="form-row">
        <div class="form-group">
          <label>Pickup Location *</label>
          <input [(ngModel)]="newBooking.pickupLocation" name="pickup" placeholder="Enter pickup location" required>
        </div>
        <div class="form-group">
          <label>Dropoff Location *</label>
          <input [(ngModel)]="newBooking.dropoffLocation" name="dropoff" placeholder="Enter dropoff location" required>
        </div>
      </div>
    </div>

    <button type="submit" (click)="addBooking()" class="btn-success" [disabled]="(!createdCustomerId && !useExistingCustomer) || !selectedDriverId || !selectedVehicleId">
      Create Booking
    </button>
  </div>

  <div class="content">
    <h3>Bookings List</h3>
    <div *ngIf="bookings.length > 0; else noBookings" class="bookings-grid">
      <div *ngFor="let booking of bookings" class="booking-card">
        <div class="card-header">
          <div>
            <h4>{{ getCustomerName(booking.customer?.id) }}</h4>
            <p class="date">{{ booking.pickupDateTime | date:'shortDate' }}</p>
          </div>
          <span [ngClass]="'status-' + getStatusClass(booking.status)" class="status-badge">
            {{ booking.status }}
          </span>
        </div>
        <div class="card-body">
          <div class="info-row">
            <div class="info-item">
              <span class="label">Driver</span>
              <span class="value">{{ getDriverName(booking.driver?.id) }}</span>
            </div>
            <div class="info-item">
              <span class="label">Vehicle</span>
              <span class="value">{{ getVehicleModel(booking.vehicle?.id) }}</span>
            </div>
          </div>
          <div class="location-row">
            <div class="location-item">
              <span class="icon">📍</span>
              <span class="location-text">{{ booking.pickupLocation }}</span>
            </div>
            <span class="arrow">→</span>
            <div class="location-item">
              <span class="icon">🎯</span>
              <span class="location-text">{{ booking.dropoffLocation }}</span>
            </div>
          </div>
        </div>
        <div class="card-footer">
          <button (click)="deleteBooking(booking.id)" class="btn-danger">Cancel Booking</button>
        </div>
      </div>
    </div>
    <ng-template #noBookings>
      <div class="empty-state">
        <p>No bookings found. Create a new booking to get started.</p>
      </div>
    </ng-template>
  </div>
</div>
