import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../services/api.service';
import { Driver } from '../models';

@Component({
  selector: 'app-driver',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './driver.component.html',
  styleUrls: ['./driver.component.css']
})
export class DriverComponent implements OnInit {
  drivers: Driver[] = [];
  newDriver: Driver = { name: '', phone: '', licenseNumber: '' };
  editingId: number | null = null;
  showForm = false;
  showSuccess = false;
  successMessage = '';

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.loadDrivers();
  }

  loadDrivers(): void {
    this.apiService.getDrivers().subscribe({
      next: (data) => this.drivers = data,
      error: (error) => console.error('Error loading drivers:', error)
    });
  }

  addDriver(): void {
    if (this.newDriver.name && this.newDriver.phone && this.newDriver.licenseNumber) {
      this.apiService.createDriver(this.newDriver).subscribe({
        next: () => {
          this.loadDrivers();
          this.newDriver = { name: '', phone: '', licenseNumber: '' };
          this.showForm = false;
          this.showSuccessMessage('Driver added successfully!');
        },
        error: (error) => console.error('Error adding driver:', error)
      });
    }
  }

  showSuccessMessage(message: string): void {
    this.successMessage = message;
    this.showSuccess = true;
    setTimeout(() => {
      this.showSuccess = false;
    }, 3000);
  }

  deleteDriver(id: number | undefined): void {
    if (id && confirm('Are you sure you want to delete this driver?')) {
      this.apiService.deleteDriver(id).subscribe({
        next: () => {
          this.loadDrivers();
          this.showSuccessMessage('Driver deleted successfully!');
        },
        error: (error) => console.error('Error deleting driver:', error)
      });
    }
  }

  toggleForm(): void {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.newDriver = { name: '', phone: '', licenseNumber: '' };
    }
  }
}
<div class="driver-container">
  <div class="header">
    <h2>Driver Management</h2>
    <button class="btn-primary" (click)="toggleForm()">
      {{ showForm ? '✕ Cancel' : '+ Add Driver' }}
    </button>
  </div>

  <div class="success-alert" *ngIf="showSuccess">
    ✓ {{ successMessage }}
  </div>

  <div class="form-card" *ngIf="showForm">
    <h3>Add New Driver</h3>
    <div class="form-row">
      <div class="form-group">
        <label>Full Name *</label>
        <input [(ngModel)]="newDriver.name" name="name" placeholder="Enter driver name" required>
      </div>
    </div>
    <div class="form-row">
      <div class="form-group">
        <label>Phone</label>
        <input [(ngModel)]="newDriver.phone" name="phone" placeholder="Enter phone number">
      </div>
      <div class="form-group">
        <label>License Number *</label>
        <input [(ngModel)]="newDriver.licenseNumber" name="licenseNumber" placeholder="DL123456" required>
      </div>
    </div>
    <button type="button" (click)="addDriver()" class="btn-success">Add Driver</button>
  </div>

  <div class="content">
    <h3>Drivers List</h3>
    <div *ngIf="drivers.length > 0; else noDrivers" class="cards-grid">
      <div *ngFor="let driver of drivers" class="card">
        <div class="card-header">
          <h4>{{ driver.name }}</h4>
          <span class="badge">DL: {{ driver.licenseNumber }}</span>
        </div>
        <div class="card-body">
          <p><strong>Email:</strong> {{ driver.email }}</p>
          <p><strong>Phone:</strong> {{ driver.phone || 'N/A' }}</p>
        </div>
        <div class="card-footer">
          <button (click)="deleteDriver(driver.id)" class="btn-danger">Delete</button>
        </div>
      </div>
    </div>
    <ng-template #noDrivers>
      <div class="empty-state">
        <p>No drivers found. Add a new driver to get started.</p>
      </div>
    </ng-template>
  </div>
</div>
