import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../services/api.service';
import { Customer } from '../models';

@Component({
  selector: 'app-customer',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css']
})
export class CustomerComponent implements OnInit {
  customers: Customer[] = [];
  newCustomer: Customer = { name: '', email: '', phone: '', address: '' };
  editingId: number | null = null;

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.loadCustomers();
  }

  loadCustomers(): void {
    this.apiService.getCustomers().subscribe({
      next: (data) => this.customers = data,
      error: (error) => console.error('Error loading customers:', error)
    });
  }

  addCustomer(): void {
    if (this.newCustomer.name && this.newCustomer.email) {
      this.apiService.createCustomer(this.newCustomer).subscribe({
        next: () => {
          this.loadCustomers();
          this.newCustomer = { name: '', email: '', phone: '', address: '' };
        },
        error: (error) => console.error('Error adding customer:', error)
      });
    }
  }

  deleteCustomer(id: number | undefined): void {
    if (id && confirm('Are you sure?')) {
      this.apiService.deleteCustomer(id).subscribe({
        next: () => this.loadCustomers(),
        error: (error) => console.error('Error deleting customer:', error)
      });
    }
  }
}
<h2>Customers</h2>

<div class="form-container">
  <h3>Add New Customer</h3>
  <form (ngSubmit)="addCustomer()">
    <input [(ngModel)]="newCustomer.name" name="name" placeholder="Name" required>
    <input [(ngModel)]="newCustomer.email" name="email" placeholder="Email" required>
    <input [(ngModel)]="newCustomer.phone" name="phone" placeholder="Phone">
    <input [(ngModel)]="newCustomer.address" name="address" placeholder="Address">
    <button type="submit">Add Customer</button>
  </form>
</div>

<div class="list-container">
  <h3>Customer List</h3>
  <table *ngIf="customers.length > 0; else noCustomers">
    <thead>
      <tr>
        <th>Name</th>
        <th>Email</th>
        <th>Phone</th>
        <th>Address</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let customer of customers">
        <td>{{ customer.name }}</td>
        <td>{{ customer.email }}</td>
        <td>{{ customer.phone }}</td>
        <td>{{ customer.address }}</td>
        <td>
          <button (click)="deleteCustomer(customer.id)" class="delete-btn">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
  <ng-template #noCustomers>
    <p>No customers found.</p>
  </ng-template>
</div>
