import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../services/api.service';
import { Booking, Customer, Driver, Vehicle } from '../models';

@Component({
  selector: 'app-booking',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './booking.component.html',
  styleUrls: ['./booking.component.css']
})
export class BookingComponent implements OnInit {
  bookings: Booking[] = [];
  customers: Customer[] = [];
  drivers: Driver[] = [];
  vehicles: Vehicle[] = [];
  
  newBooking: Booking = { 
    customerId: 0, 
    driverId: 0, 
    vehicleId: 0,
    pickupLocation: '',
    dropoffLocation: '',
    bookingDate: '',
    status: 'Pending'
  };
  
  newCustomerData: Customer = {
    name: '',
    email: '',
    phone: '',
    address: ''
  };

  showForm = false;
  useExistingCustomer = true;
  createdCustomerId: number | null = null;

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.loadBookings();
    this.loadDropdownData();
  }

  loadBookings(): void {
    this.apiService.getBookings().subscribe({
      next: (data) => this.bookings = data,
      error: (error) => console.error('Error loading bookings:', error)
    });
  }

  loadDropdownData(): void {
    this.apiService.getCustomers().subscribe({
      next: (data) => this.customers = data,
      error: (error) => console.error('Error loading customers:', error)
    });

    this.apiService.getDrivers().subscribe({
      next: (data) => this.drivers = data,
      error: (error) => console.error('Error loading drivers:', error)
    });

    this.apiService.getVehicles().subscribe({
      next: (data) => this.vehicles = data,
      error: (error) => console.error('Error loading vehicles:', error)
    });
  }

  createNewCustomer(): void {
    if (this.newCustomerData.name && this.newCustomerData.email) {
      this.apiService.createCustomer(this.newCustomerData).subscribe({
        next: (customer) => {
          this.createdCustomerId = customer.id || 0;
          this.newBooking.customerId = this.createdCustomerId;
          this.customers.push(customer);
          this.newCustomerData = { name: '', email: '', phone: '', address: '' };
          console.log('Customer created successfully', customer);
        },
        error: (error) => console.error('Error creating customer:', error)
      });
    }
  }

  addBooking(): void {
    let customerId = this.useExistingCustomer ? this.newBooking.customerId : this.createdCustomerId;

    if (customerId && this.newBooking.driverId && this.newBooking.vehicleId &&
        this.newBooking.pickupLocation && this.newBooking.dropoffLocation && this.newBooking.bookingDate) {
      
      const bookingToCreate = { ...this.newBooking, customerId: customerId };
      
      this.apiService.createBooking(bookingToCreate).subscribe({
        next: () => {
          this.loadBookings();
          this.resetForm();
        },
        error: (error) => console.error('Error adding booking:', error)
      });
    }
  }

  resetForm(): void {
    this.newBooking = { 
      customerId: 0, 
      driverId: 0, 
      vehicleId: 0,
      pickupLocation: '',
      dropoffLocation: '',
      bookingDate: '',
      status: 'Pending'
    };
    this.newCustomerData = { name: '', email: '', phone: '', address: '' };
    this.createdCustomerId = null;
    this.useExistingCustomer = true;
    this.showForm = false;
  }

  deleteBooking(id: number | undefined): void {
    if (id && confirm('Are you sure you want to cancel this booking?')) {
      this.apiService.deleteBooking(id).subscribe({
        next: () => this.loadBookings(),
        error: (error) => console.error('Error deleting booking:', error)
      });
    }
  }

  toggleForm(): void {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.resetForm();
    }
  }

  getCustomerName(id: number): string {
    return this.customers.find(c => c.id === id)?.name || 'N/A';
  }

  getDriverName(id: number): string {
    return this.drivers.find(d => d.id === id)?.name || 'N/A';
  }

  getVehicleModel(id: number): string {
    return this.vehicles.find(v => v.id === id)?.model || 'N/A';
  }

  getStatusClass(status: string): string {
    return status.toLowerCase().replace(' ', '-');
  }
}
<div class="booking-container">
  <div class="header">
    <h2>Booking Management</h2>
    <button class="btn-primary" (click)="toggleForm()">
      {{ showForm ? '‚úï Cancel' : '+ New Booking' }}
    </button>
  </div>

  <div class="form-card" *ngIf="showForm">
    <h3>Create New Booking</h3>
    <form (ngSubmit)="addBooking()">
      <div class="form-row">
        <div class="form-group">
          <label>Customer *</label>
          <select [(ngModel)]="newBooking.customerId" name="customer" required>
            <option value="" disabled>Select a customer</option>
            <option *ngFor="let customer of customers" [value]="customer.id">
              {{ customer.name }} ({{ customer.email }})
            </option>
          </select>
        </div>
        <div class="form-group">
          <label>Driver *</label>
          <select [(ngModel)]="newBooking.driverId" name="driver" required>
            <option value="" disabled>Select a driver</option>
            <option *ngFor="let driver of drivers" [value]="driver.id">
              {{ driver.name }} ({{ driver.licenseNumber }})
            </option>
          </select>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label>Vehicle *</label>
          <select [(ngModel)]="newBooking.vehicleId" name="vehicle" required>
            <option value="" disabled>Select a vehicle</option>
            <option *ngFor="let vehicle of vehicles" [value]="vehicle.id">
              {{ vehicle.model }} ({{ vehicle.registrationNumber }})
            </option>
          </select>
        </div>
        <div class="form-group">
          <label>Booking Date *</label>
          <input [(ngModel)]="newBooking.bookingDate" name="date" type="date" required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label>Pickup Location *</label>
          <input [(ngModel)]="newBooking.pickupLocation" name="pickup" placeholder="Enter pickup location" required>
        </div>
        <div class="form-group">
          <label>Dropoff Location *</label>
          <input [(ngModel)]="newBooking.dropoffLocation" name="dropoff" placeholder="Enter dropoff location" required>
        </div>
      </div>

      <div class="form-group">
        <label>Status *</label>
        <select [(ngModel)]="newBooking.status" name="status" required>
          <option value="Pending">Pending</option>
          <option value="Confirmed">Confirmed</option>
          <option value="Completed">Completed</option>
          <option value="Cancelled">Cancelled</option>
        </select>
      </div>

      <button type="submit" class="btn-success">Create Booking</button>
    </form>
  </div>

  <div class="content">
    <h3>Bookings List</h3>
    <div *ngIf="bookings.length > 0; else noBookings" class="bookings-grid">
      <div *ngFor="let booking of bookings" class="booking-card">
        <div class="card-header">
          <div>
            <h4>{{ getCustomerName(booking.customerId) }}</h4>
            <p class="date">{{ booking.bookingDate | date:'shortDate' }}</p>
          </div>
          <span [ngClass]="'status-' + getStatusClass(booking.status)" class="status-badge">
            {{ booking.status }}
          </span>
        </div>
        <div class="card-body">
          <div class="info-row">
            <div class="info-item">
              <span class="label">Driver</span>
              <span class="value">{{ getDriverName(booking.driverId) }}</span>
            </div>
            <div class="info-item">
              <span class="label">Vehicle</span>
              <span class="value">{{ getVehicleModel(booking.vehicleId) }}</span>
            </div>
          </div>
          <div class="location-row">
            <div class="location-item">
              <span class="icon">üìç</span>
              <span class="location-text">{{ booking.pickupLocation }}</span>
            </div>
            <span class="arrow">‚Üí</span>
            <div class="location-item">
              <span class="icon">üéØ</span>
              <span class="location-text">{{ booking.dropoffLocation }}</span>
            </div>
          </div>
        </div>
        <div class="card-footer">
          <button (click)="deleteBooking(booking.id)" class="btn-danger">Cancel Booking</button>
        </div>
      </div>
    </div>
    <ng-template #noBookings>
      <div class="empty-state">
        <p>No bookings found. Create a new booking to get started.</p>
      </div>
    </ng-template>
  </div>
</div>
