import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../services/api.service';
import { Vehicle } from '../models';

@Component({
  selector: 'app-vehicle',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './vehicle.component.html',
  styleUrls: ['./vehicle.component.css']
})
export class VehicleComponent implements OnInit {
  vehicles: Vehicle[] = [];
  newVehicle: Vehicle = { registrationNumber: '', model: '', capacity: 0, status: 'Available' };
  showForm = false;
  showSuccess = false;
  successMessage = '';

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.loadVehicles();
  }

  loadVehicles(): void {
    this.apiService.getVehicles().subscribe({
      next: (data) => this.vehicles = data,
      error: (error) => console.error('Error loading vehicles:', error)
    });
  }

  addVehicle(): void {
    if (this.newVehicle.registrationNumber && this.newVehicle.model && this.newVehicle.capacity > 0) {
      this.apiService.createVehicle(this.newVehicle).subscribe({
        next: () => {
          this.loadVehicles();
          this.newVehicle = { registrationNumber: '', model: '', capacity: 0, status: 'Available' };
          this.showForm = false;
          this.showSuccessMessage('Vehicle added successfully!');
        },
        error: (error) => console.error('Error adding vehicle:', error)
      });
    }
  }

  showSuccessMessage(message: string): void {
    this.successMessage = message;
    this.showSuccess = true;
    setTimeout(() => {
      this.showSuccess = false;
    }, 3000);
  }

  deleteVehicle(id: number | undefined): void {
    if (id && confirm('Are you sure you want to delete this vehicle?')) {
      this.apiService.deleteVehicle(id).subscribe({
        next: () => {
          this.loadVehicles();
          this.showSuccessMessage('Vehicle deleted successfully!');
        },
        error: (error) => console.error('Error deleting vehicle:', error)
      });
    }
  }

  toggleForm(): void {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.newVehicle = { registrationNumber: '', model: '', capacity: 0, status: 'Available' };
    }
  }

  getStatusClass(status: string): string {
    return status.toLowerCase().replace(' ', '-');
  }
}
<div class="vehicle-container">
  <div class="header">
    <h2>Vehicle Management</h2>
    <button class="btn-primary" (click)="toggleForm()">
      {{ showForm ? '✕ Cancel' : '+ Add Vehicle' }}
    </button>
  </div>

  <div class="form-card" *ngIf="showForm">
    <h3>Add New Vehicle</h3>
    <div class="form-row">
      <div class="form-group">
        <label>Registration Number *</label>
        <input [(ngModel)]="newVehicle.registrationNumber" name="reg" placeholder="AP 01 AB 1234" required>
      </div>
      <div class="form-group">
        <label>Model *</label>
        <input [(ngModel)]="newVehicle.model" name="model" placeholder="e.g., Toyota Innova" required>
      </div>
    </div>
    <div class="form-row">
      <div class="form-group">
        <label>Capacity *</label>
        <input [(ngModel)]="newVehicle.capacity" name="capacity" type="number" placeholder="Number of seats" required>
      </div>
      <div class="form-group">
        <label>Status *</label>
        <select [(ngModel)]="newVehicle.status" name="status" required>
          <option value="Available">Available</option>
          <option value="In Use">In Use</option>
          <option value="Maintenance">Maintenance</option>
        </select>
      </div>
    </div>
    <button type="button" (click)="addVehicle()" class="btn-success">Add Vehicle</button>
  </div>

  <div class="content">
    <h3>Vehicles List</h3>
    <div *ngIf="vehicles.length > 0; else noVehicles" class="table-responsive">
      <table class="vehicles-table">
        <thead>
          <tr>
            <th>Registration</th>
            <th>Model</th>
            <th>Capacity</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let vehicle of vehicles">
            <td><strong>{{ vehicle.registrationNumber }}</strong></td>
            <td>{{ vehicle.model }}</td>
            <td><span class="capacity-badge">{{ vehicle.capacity }} Seats</span></td>
            <td>
              <span [ngClass]="'status-' + getStatusClass(vehicle.status)" class="status-badge">
                {{ vehicle.status }}
              </span>
            </td>
            <td>
              <button (click)="deleteVehicle(vehicle.id)" class="btn-danger">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
    <ng-template #noVehicles>
